<script lang="ts">
import { ref, defineComponent, toRefs, reactive } from "vue";
export default defineComponent({
  props: {
    text: { type: String, default: "" },
    content: { type: String, default: "" },
  },
  setup(props, context) {
    const root = ref();
    const fullRef = ref();
    const state = reactive({
      visible: false,
      modalHeight: document.body.clientHeight - 114 + "px",
      modalWidth: 800 + 'px',
      isFull: false,
    });

    const close = () => {
      state.modalHeight = (document.body.clientHeight - 114) + 'px'
      state.modalWidth = 800 + 'px'
      state.isFull = false
    }

    const toggle = () => {
      console.log(fullRef.value);
      const ro = new ResizeObserver((entries, observer) => {
        for (const entry of entries) {
          const { left, top, width, height } = entry.contentRect;
          console.log(left, top, width, height);
          fullRef.value.height = height + 'px'
          state.modalHeight = (height - 48) + 'px'
          state.modalWidth = width + 'px'
          state.isFull = true
          // console.log('Element:', entry.target);
          // console.log(`Element's size: ${width}px x ${height}px`);
          // console.log(`Element's paddings: ${top}px ; ${left}px`);
        }
      });

      // ro.observe(fullRef.value);
      ro.observe(document.body);
    };
    // async function toggle() {
    //   await fullscreen.toggle(root.value.querySelector('.fullscreen-wrapper'), {
    //     teleport: state.teleport,
    //     pageOnly: true,
    //     callback: (isFullscreen) => {
    //       state.visible = true
    //       state.fullscreen = isFullscreen
    //     },
    //   })
    //   state.fullscreen = fullscreen.isFullscreen
    // }

    const showVisible = () => {
      state.visible = true;
      // initResize()
    };

    return {
      root,
      props,
      fullRef,
      ...toRefs(state),
      toggle,
      close,
      showVisible,
    };
  },
});
</script>
